package com.caiyi.mycalendar.fragment;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import com.caiyi.mycalendar.R;import com.caiyi.mycalendar.base.BaseFragment;import com.caiyi.mycalendar.compant.MeiziMessageActivity;import butterknife.ButterKnife;import butterknife.Unbinder;/** * Created by Renzeqiang . * 2017/7/29 */public class GirlFragment extends BaseFragment {    private Unbinder unbinder;    @Nullable    @Override    public View onCreateView(LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        View view = LayoutInflater.from(getContext()).inflate(R.layout.fragment_girl, null);        unbinder = ButterKnife.bind(this, view);        initView(view);        return view;    }    private void initView(View view) {        setViewClickListeners(view, R.id.iv_meiziMessage);    }    @Override    public void onClick(View v) {        switch (v.getId()) {            case R.id.iv_meiziMessage:                openActivity(MeiziMessageActivity.class);                break;        }    }    @Override    public void onDestroyView() {        super.onDestroyView();        if (unbinder != null) {            unbinder.unbind();        }    }}