package com.caiyi.mycalendar.fragment;import android.net.Uri;import android.os.Bundle;import android.support.annotation.Nullable;import android.view.Gravity;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import com.caiyi.mycalendar.R;import com.caiyi.mycalendar.Utils.GlobalConstants;import com.caiyi.mycalendar.base.BaseFragment;import com.caiyi.mycalendar.compant.MainActivity;import com.facebook.drawee.view.SimpleDraweeView;/** * Created by Renzeqiang . * 2017/7/29 */public class HomeFragment extends BaseFragment {    private SimpleDraweeView myPhoto;    @Nullable    @Override    public View onCreateView(LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        View view = LayoutInflater.from(getContext()).inflate(R.layout.fragment_home, null);        initView(view);        return view;    }    private void initView(View view) {        myPhoto = (SimpleDraweeView) view.findViewById(R.id.sdv_myPhoto);        myPhoto.setImageURI(Uri.parse(GlobalConstants.GLOBAL_URL.PHOTO_URL));        setViewClickListeners(view, R.id.sdv_myPhoto);    }    @Override    public void onClick(View v) {        switch (v.getId()) {            case R.id.sdv_myPhoto:                if (!MainActivity.mDrawerLayout.isDrawerOpen(Gravity.LEFT)) {                    MainActivity.mDrawerLayout.openDrawer(Gravity.LEFT);                }                break;            default:                break;        }    }}