package com.caiyi.mycalendar.fragment;import android.net.Uri;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import com.caiyi.mycalendar.R;import com.facebook.drawee.view.SimpleDraweeView;/** * Created by Renzeqiang . * 2017/7/29 */public class HomeFragment extends Fragment {    private SimpleDraweeView myPhoto;    private String url = "http://img1.touxiang.cn/uploads/20120428/28-051412_566.jpg";    @Nullable    @Override    public View onCreateView(LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        View view = LayoutInflater.from(getContext()).inflate(R.layout.fragment_home, null);        myPhoto = (SimpleDraweeView) view.findViewById(R.id.sdv_myPhoto);        myPhoto.setImageURI(Uri.parse(url));        return view;    }}