package com.caiyi.mycalendar.fragment;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import com.caiyi.mycalendar.R;import com.caiyi.mycalendar.base.BaseFragment;import com.caiyi.mycalendar.compant.SettingActivity;/** * Created by Renzeqiang . * 2017/7/29 */public class UserCenterFragment extends BaseFragment {    @Nullable    @Override    public View onCreateView(LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        View view = LayoutInflater.from(getContext()).inflate(R.layout.fragment_usercenter, null);        setViewClickListeners(view, R.id.tv_setting);        return view;    }    @Override    public void onClick(View v) {        switch (v.getId()) {            case R.id.tv_setting:                openActivity(SettingActivity.class);                break;            default:break;        }    }}